/** isStringRecord tests if an obj is a Record<string, unknown>. */
export const isStringRecord = (obj) => {
    if (typeof obj !== "object" || obj == null) {
        return false;
    }
    if (Array.isArray(obj)) {
        return false;
    }
    // Handle functions.
    if (Object.getOwnPropertySymbols(obj).length > 0) {
        return false;
    }
    return true;
};
