import { Client } from "./client";
export const getRun = async (runID, opts = {}) => {
    const client = new Client(opts);
    const run = await client.getRun(runID);
    const useZone = !!run.zoneID;
    const runOutput = await client.getRunOutput(runID, { useZone });
    return {
        id: runID,
        taskID: run.taskID,
        paramValues: run.paramValues,
        status: run.status,
        output: runOutput,
    };
};
export const getSession = async (sessionID, opts = {}) => {
    const client = new Client(opts);
    const session = await client.getSession(sessionID);
    return {
        id: sessionID,
        runbookID: session.runbookID,
        paramValues: session.paramValues,
        status: session.status,
    };
};
