import { getRuntime } from "../runtime";
/**
 * Appends a display to the run that renders a file as a download link. If the file is a text,
 * csv, image, video, or audio file, it will also be shown inline alongside the download link.
 * @param file File to render
 */
export const file = async (file) => {
    await getRuntime().display({
        kind: "file",
        uploadID: file.id,
    });
};
