import { dedent } from "ts-dedent";
import { getRuntime } from "../runtime";
/**
 * Appends a display to the run that renders the provided markdown text.
 *
 * See the [CommonMark docs](https://commonmark.org/help/) for an introduction
 * to markdown formatting.
 * @param content Text to render as markdown
 */
export const text = async (content) => {
    await getRuntime().display({
        kind: "markdown",
        content: dedent(content),
    });
};
/**
 * Appends a display to the run that renders the provided markdown text.
 *
 * See the [CommonMark docs](https://commonmark.org/help/) for an introduction
 * to markdown formatting.
 * @param content Text to render as markdown
 * @deprecated replace with `.text(content)`
 */
export const markdown = async (content) => {
    await getRuntime().display({
        kind: "markdown",
        content: dedent(content),
    });
};
