export const isStatusTerminal = (status) => {
    switch (status) {
        case "Succeeded":
        case "Failed":
        case "Cancelled":
            return true;
        default:
            return false;
    }
};
export const isRequestStatusTerminal = (status) => {
    switch (status) {
        case "approved":
        case "rejected":
            return true;
        default:
            return false;
    }
};
export const isSessionStatusTerminal = (status) => {
    switch (status) {
        case "Succeeded":
        case "Failed":
        case "Cancelled":
            return true;
        default:
            return false;
    }
};
